package main

import (
	"fmt"
	"log"
)

func unique(s string) bool {
	log.Printf("%q", s)
	m := map[rune]struct{}{}
	for _, c := range s {
		if _, ok := m[c]; ok {
			return false
		}
		m[c] = struct{}{}
	}
	return true
}

func run(input string) (int, error) {
	for i := 4; i < len(input); i++ {
		if unique(input[i-4 : i]) {
			return i, nil
		}
	}
	return 0, fmt.Errorf("not found")
}

